import streamlit as st
import pandas as pd

###################################
from st_aggrid import AgGrid
from st_aggrid.grid_options_builder import GridOptionsBuilder
from st_aggrid.shared import JsCode

###################################
from functionforDownloadButtons import download_button

###################################

st.set_page_config(page_title="üìä CSV Wrangler", layout="wide")
st.title("üìä CSV Wrangler")

with st.expander("CHECK NOTES", expanded=False):
    st.markdown(
        """
* using[biostats.csv](https://people.sc.fsu.edu/~jburkardt/data/csv/biostats.csv)as sample


‚ÑπÔ∏è - To-do (suggested by Charly)
-   Add filtering ability from dropdowns  \

    * it may not look intuive, but this option is here: \
    
    ![]( data:image/png;base64,)


-   Improve export to CSV \

    Ideas?
	    """
    )

c29, c30, c31 = st.columns([1, 6, 1])

with c30:

    uploaded_file = st.file_uploader("", key="1")

    if uploaded_file is not None:
        file_container = st.expander("Check your uploaded CSV")
        shows = pd.read_csv(uploaded_file)
        uploaded_file.seek(0)
        file_container.write(shows)

    else:
        st.success(
            f"""
                üëÜ Upload a csv file
                """
        )
        st.stop()

from st_aggrid import GridUpdateMode, DataReturnMode

gb = GridOptionsBuilder.from_dataframe(shows)
#enables pivoting on all columns, however i'd need to change ag grid to allow export of pivoted/grouped data, however it select/filters groups
gb.configure_default_column(enablePivot=True, enableValue=True, enableRowGroup=True) 
gb.configure_selection(selection_mode="multiple", use_checkbox=True)
gb.configure_side_bar() #side_bar is clearly a typo :) should by sidebar
gridOptions = gb.build()

response = AgGrid(
    shows,
    gridOptions=gridOptions,
    enable_enterprise_modules=True,
    update_mode=GridUpdateMode.MODEL_CHANGED,
    data_return_mode=DataReturnMode.FILTERED_AND_SORTED,
    fit_columns_on_grid_load=True
)

df = pd.DataFrame(response['selected_rows'])

st.subheader('Data to export')
st.write(df)

CSVButton = download_button(
    df,
    "File.csv",
    "‚ú® Download filtered to CSV",
)
